function checkPrimeNumbers(numbersArray) {
    // Function to check if a number is prime
    const isPrime = num => {
        if (num <= 1) return false;
        if (num <= 3) return true;
        if (num % 2 === 0 || num % 3 === 0) return false;

        for (let i = 5; i * i <= num; i += 6) {
            if (num % i === 0 || num % (i + 2) === 0) return false;
        }

        return true;
    };

    // Ensure the array contains exactly 5 numbers
    if (!Array.isArray(numbersArray) || numbersArray.length !== 5) {
        console.error("Input must be an array with exactly 5 numbers.");
        return;
    }

    // Check each number and log if it's prime
    numbersArray.forEach(number => {
        if (isPrime(number)) {
            console.log(number);
        }
    });
}

// Example usage:
checkPrimeNumbers([3, 5, 8, 13, 26]); // Outputs: 3, 5, 13
```

