```cpp
#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

// Define the structure for the edges
struct Edge {
    int to, weight;
    Edge(int t, int w) : to(t), weight(w) {}
};

// Define the structure for the priority queue
struct Node {
    int vertex, distance;
    Node(int v, int d) : vertex(v), distance(d) {}
    // Operator overloading for priority comparison
    bool operator>(const Node& node) const {
        return distance > node.distance;
    }
};

void Dijkstra(const vector<vector<Edge>>& graph, int src) {
    int numVertices = graph.size();
    vector<int> dist(numVertices, numeric_limits<int>::max()); // Distance array
    vector<bool> visited(numVertices, false); // Visited vertices

    priority_queue<Node, vector<Node>, greater<Node>> pq; // Min-heap based on distance

    // Initialize the source vertex
    pq.push(Node(src, 0));
    dist[src] = 0;

    while (!pq.empty()) {
        // Select the vertex with the smallest distance
        int u = pq.top().vertex;
        pq.pop();
        if (visited[u]) continue; // Skip if already visited
        visited[u] = true; // Mark as visited

        // Relaxation step for adjacent vertices
        for (auto& edge : graph[u]) {
            int v = edge.to;
            int weight = edge.weight;

            // If a shorter path is found
            if (!visited[v] && dist[u] + weight < dist[v]) {
                dist[v] = dist[u] + weight; // Update distance
                pq.push(Node(v, dist[v])); // Push the updated distance to the priority queue
            }
        }
    }

    // Print the shortest distances
    cout << "Vertex\tDistance from source" << endl;
    for (int i = 0; i < numVertices; ++i) {
        cout << i << "\t" << dist[i] << endl;
    }
}

