    head->next = createNode(2);
    head->next->next = createNode(3);
    head->next->next->next = createNode(4);

    printf("Original linked list: ");
    printList(head);

    head = reverseLinkedList(head);

    printf("Reversed linked list: ");
    printList(head);

    return 0;
}
